mapper.insert:
insert into ad_day_result (id,org_code,org_name,employee,real_name,ad_date,shift_name,state,state_name,times,minutes,work_minutes,shift_id,employee_no) select SEQUENCE_AD_DAY_RESULT.nextval,cd.* from(
select #{item.orgCode,jdbcType=INTEGER},#{item.orgName,jdbcType=VARCHAR},#{item.employee,jdbcType=VARCHAR},#{item.realName,jdbcType=VARCHAR},#{item.adDate,jdbcType=DATE},#{item.shiftName,jdbcType=VARCHAR},#{item.state,jdbcType=INTEGER},#{item.stateName,jdbcType=VARCHAR},#{item.times,jdbcType=INTEGER},#{item.minutes,jdbcType=INTEGER},#{item.workMinutes,jdbcType=INTEGER},#{item.shiftId,jdbcType=INTEGER},#{item.employeeNo,jdbcType=VARCHAR} from dual
) cd

mapper.update:
update ad_day_result
org_code = #{orgCode,jdbcType=INTEGER}, org_name = #{orgName,jdbcType=VARCHAR}, employee = #{employee,jdbcType=VARCHAR}, real_name = #{realName,jdbcType=VARCHAR}, ad_date = #{adDate,jdbcType=DATE}, shift_name = #{shiftName,jdbcType=VARCHAR}, state = #{state,jdbcType=INTEGER}, state_name = #{stateName,jdbcType=VARCHAR}, times = #{times,jdbcType=INTEGER}, minutes = #{minutes,jdbcType=INTEGER}, work_minutes = #{workMinutes,jdbcType=INTEGER}, shift_id = #{shiftId,jdbcType=INTEGER}, employee_no = #{employeeNo,jdbcType=VARCHAR},
where id = #{id,jdbcType=INTEGER}

mapper.select:

where 1=1 
    and ad_day_result.id = #{id,jdbcType=INTEGER} 
    and ad_day_result.org_code = #{orgCode,jdbcType=INTEGER} 
    and ad_day_result.org_name like #{orgName,jdbcType=VARCHAR} 
    and ad_day_result.employee like #{employee,jdbcType=VARCHAR} 
    and ad_day_result.real_name like #{realName,jdbcType=VARCHAR} 
    and ad_day_result.ad_date like #{adDate,jdbcType=DATE} 
    and ad_day_result.shift_name like #{shiftName,jdbcType=VARCHAR} 
    and ad_day_result.state = #{state,jdbcType=INTEGER} 
    and ad_day_result.state_name like #{stateName,jdbcType=VARCHAR} 
    and ad_day_result.times = #{times,jdbcType=INTEGER} 
    and ad_day_result.minutes = #{minutes,jdbcType=INTEGER} 
    and ad_day_result.work_minutes = #{workMinutes,jdbcType=INTEGER} 
    and ad_day_result.shift_id = #{shiftId,jdbcType=INTEGER} 
    and ad_day_result.employee_no like #{employeeNo,jdbcType=VARCHAR} 
    and ad_employee.is_attendance = #{isAttendance,jdbcType=TINYINT} 
    and employee_no_sys_user.real_name like #{employeeNoAdEmployeeRealName,jdbcType=VARCHAR}
