mapper.insert:
insert into ad_exception (id,shift_id,end_date,start_date,week_day,description) select SEQUENCE_AD_EXCEPTION.nextval,cd.* from(
select #{item.shiftId,jdbcType=INTEGER},#{item.endDate,jdbcType=DATE},#{item.startDate,jdbcType=DATE},#{item.weekDay,jdbcType=INTEGER},#{item.description,jdbcType=VARCHAR} from dual
) cd

mapper.update:
update ad_exception
shift_id = #{shiftId,jdbcType=INTEGER}, end_date = #{endDate,jdbcType=DATE}, start_date = #{startDate,jdbcType=DATE}, week_day = #{weekDay,jdbcType=INTEGER}, description = #{description,jdbcType=VARCHAR},
where id = #{id,jdbcType=INTEGER}

mapper.select:

where 1=1 
    and id = #{id,jdbcType=INTEGER} 
    and shift_id = #{shiftId,jdbcType=INTEGER} 
    and end_date like #{endDate,jdbcType=DATE} 
    and start_date like #{startDate,jdbcType=DATE} 
    and week_day = #{weekDay,jdbcType=INTEGER} 
    and description like #{description,jdbcType=VARCHAR}
