mapper.insert:
insert into ad_shift (id,name,ad_mode,start_date,end_date,minutes_per_day,description) select SEQUENCE_AD_SHIFT.nextval,cd.* from(
select #{item.name,jdbcType=VARCHAR},#{item.adMode,jdbcType=INTEGER},#{item.startDate,jdbcType=DATE},#{item.endDate,jdbcType=DATE},#{item.minutesPerDay,jdbcType=INTEGER},#{item.description,jdbcType=VARCHAR} from dual
) cd

mapper.update:
update ad_shift
name = #{name,jdbcType=VARCHAR}, ad_mode = #{adMode,jdbcType=INTEGER}, start_date = #{startDate,jdbcType=DATE}, end_date = #{endDate,jdbcType=DATE}, minutes_per_day = #{minutesPerDay,jdbcType=INTEGER}, description = #{description,jdbcType=VARCHAR},
where id = #{id,jdbcType=INTEGER}

mapper.select:

where 1=1 
    and id = #{id,jdbcType=INTEGER} 
    and name like #{name,jdbcType=VARCHAR} 
    and ad_mode = #{adMode,jdbcType=INTEGER} 
    and start_date like #{startDate,jdbcType=DATE} 
    and end_date like #{endDate,jdbcType=DATE} 
    and minutes_per_day = #{minutesPerDay,jdbcType=INTEGER} 
    and description like #{description,jdbcType=VARCHAR}
