mapper.insert:
insert into ad_shift_time (id,shift_id,name,week_days,start_work_time,end_work_time) select SEQUENCE_AD_SHIFT_TIME.nextval,cd.* from(
select #{item.shiftId,jdbcType=INTEGER},#{item.name,jdbcType=VARCHAR},#{item.weekDays,jdbcType=VARCHAR},#{item.startWorkTime,jdbcType=TIME},#{item.endWorkTime,jdbcType=TIME} from dual
) cd

mapper.update:
update ad_shift_time
shift_id = NULL, shift_id = #{shiftId,jdbcType=INTEGER}, name = NULL, name = #{name,jdbcType=VARCHAR}, week_days = NULL, week_days = #{weekDays,jdbcType=VARCHAR}, start_work_time = NULL, start_work_time = #{startWorkTime,jdbcType=TIME}, end_work_time = NULL, end_work_time = #{endWorkTime,jdbcType=TIME},
where id = #{id,jdbcType=INTEGER}

mapper.select:

where 1=1 
    and id = #{id,jdbcType=INTEGER} 
    and shift_id = #{shiftId,jdbcType=INTEGER} 
    and name like #{name,jdbcType=VARCHAR} 
    and week_days like #{weekDays,jdbcType=VARCHAR} 
    and start_work_time like #{startWorkTime,jdbcType=TIME} 
    and end_work_time like #{endWorkTime,jdbcType=TIME}
