mapper.insert:
insert into ad_special_shift (id,shift_mode,name,mode_property,start_date,end_date,org_codes,sex_property,age_property0,age_property1,level_property0,level_property1,description) select SEQUENCE_AD_SPECIAL_SHIFT.nextval,cd.* from(
select #{item.shiftMode,jdbcType=INTEGER},#{item.name,jdbcType=VARCHAR},#{item.modeProperty,jdbcType=INTEGER},#{item.startDate,jdbcType=DATE},#{item.endDate,jdbcType=DATE},#{item.orgCodes,jdbcType=VARCHAR},#{item.sexProperty,jdbcType=INTEGER},#{item.ageProperty0,jdbcType=INTEGER},#{item.ageProperty1,jdbcType=INTEGER},#{item.levelProperty0,jdbcType=INTEGER},#{item.levelProperty1,jdbcType=INTEGER},#{item.description,jdbcType=VARCHAR} from dual
) cd

mapper.update:
update ad_special_shift
shift_mode = NULL, shift_mode = #{shiftMode,jdbcType=INTEGER}, name = NULL, name = #{name,jdbcType=VARCHAR}, mode_property = NULL, mode_property = #{modeProperty,jdbcType=INTEGER}, start_date = NULL, start_date = #{startDate,jdbcType=DATE}, end_date = NULL, end_date = #{endDate,jdbcType=DATE}, org_codes = NULL, org_codes = #{orgCodes,jdbcType=VARCHAR}, sex_property = NULL, sex_property = #{sexProperty,jdbcType=INTEGER}, age_property0 = NULL, age_property0 = #{ageProperty0,jdbcType=INTEGER}, age_property1 = NULL, age_property1 = #{ageProperty1,jdbcType=INTEGER}, level_property0 = NULL, level_property0 = #{levelProperty0,jdbcType=INTEGER}, level_property1 = NULL, level_property1 = #{levelProperty1,jdbcType=INTEGER}, description = NULL, description = #{description,jdbcType=VARCHAR},
where id = #{id,jdbcType=INTEGER}

mapper.select:

where 1=1 
    and id = #{id,jdbcType=INTEGER} 
    and shift_mode = #{shiftMode,jdbcType=INTEGER} 
    and name like #{name,jdbcType=VARCHAR} 
    and mode_property = #{modeProperty,jdbcType=INTEGER} 
    and start_date like #{startDate,jdbcType=DATE} 
    and end_date like #{endDate,jdbcType=DATE} 
    and org_codes like #{orgCodes,jdbcType=VARCHAR} 
    and sex_property = #{sexProperty,jdbcType=INTEGER} 
    and age_property0 = #{ageProperty0,jdbcType=INTEGER} 
    and age_property1 = #{ageProperty1,jdbcType=INTEGER} 
    and level_property0 = #{levelProperty0,jdbcType=INTEGER} 
    and level_property1 = #{levelProperty1,jdbcType=INTEGER} 
    and description like #{description,jdbcType=VARCHAR}
