mapper.insert:
insert into ad_special_shift_time (id,special_shift_id,name,start_time,end_time) select SEQUENCE_AD_SPECIAL_SHIFT_TIME.nextval,cd.* from(
select #{item.specialShiftId,jdbcType=INTEGER},#{item.name,jdbcType=VARCHAR},#{item.startTime,jdbcType=TIME},#{item.endTime,jdbcType=TIME} from dual
) cd

mapper.update:
update ad_special_shift_time set 
  special_shift_id = #{specialShiftId,jdbcType=INTEGER},
  name = #{name,jdbcType=VARCHAR},
  start_time = #{startTime,jdbcType=TIME},
  end_time = #{endTime,jdbcType=TIME}
  where id = #{id,jdbcType=INTEGER}

update ad_special_shift_time
special_shift_id = NULL, special_shift_id = #{specialShiftId,jdbcType=INTEGER}, name = NULL, name = #{name,jdbcType=VARCHAR}, start_time = NULL, start_time = #{startTime,jdbcType=TIME}, end_time = NULL, end_time = #{endTime,jdbcType=TIME},
where id = #{id,jdbcType=INTEGER}

mapper.select:

where 1=1 
    and id = #{id,jdbcType=INTEGER} 
    and special_shift_id = #{specialShiftId,jdbcType=INTEGER} 
    and name like #{name,jdbcType=VARCHAR} 
    and start_time like #{startTime,jdbcType=TIME} 
    and end_time like #{endTime,jdbcType=TIME}
