mapper.update:
update ck_sign_in
org_code = NULL, org_code = #{orgCode,jdbcType=INTEGER}, rec_source = NULL, rec_source = #{recSource,jdbcType=VARCHAR}, rec_source_type = NULL, rec_source_type = #{recSourceType,jdbcType=VARCHAR}, event_type = NULL, event_type = #{eventType,jdbcType=INTEGER}, rec_time = NULL, rec_time = #{recTime,jdbcType=TIMESTAMP}, user_no = #{userNo,jdbcType=VARCHAR},
where id = #{id,jdbcType=VARCHAR}

mapper.select:

where 1=1 
    and ck_sign_in.id like #{id,jdbcType=VARCHAR} 
    and ck_sign_in.org_code = #{orgCode,jdbcType=INTEGER} 
    and ck_sign_in.rec_source like #{recSource,jdbcType=VARCHAR} 
    and ck_sign_in.rec_source_type like #{recSourceType,jdbcType=VARCHAR} 
    and ck_sign_in.event_type = #{eventType,jdbcType=INTEGER} 
    and ck_sign_in.rec_time like #{recTime,jdbcType=TIMESTAMP} 
    and ck_sign_in.user_no like #{userNo,jdbcType=VARCHAR} 
    and dev_people.real_name like #{realName,jdbcType=VARCHAR} 
    and dev_people.sex like #{sex,jdbcType=VARCHAR} 
    and dev_people.birth_date = #{birthDate,jdbcType=DATE}
