mapper.update:
update cg_button
table_id = #{tableId,jdbcType=VARCHAR}, order_num = #{orderNum,jdbcType=INTEGER}, action = #{action,jdbcType=VARCHAR}, title = #{title,jdbcType=VARCHAR}, icon = #{icon,jdbcType=VARCHAR}, append_class = #{appendClass,jdbcType=VARCHAR}, action_property = #{actionProperty,jdbcType=VARCHAR}, row_property = #{rowProperty,jdbcType=VARCHAR}, display_properties = #{displayProperties,jdbcType=VARCHAR}, confirm_text = #{confirmText,jdbcType=VARCHAR}, is_refresh_list = #{isRefreshList,jdbcType=TINYINT},
where id = #{id,jdbcType=VARCHAR}

mapper.select:

where 1=1 
    and id like #{id,jdbcType=VARCHAR} 
    and table_id like #{tableId,jdbcType=VARCHAR} 
    and order_num = #{orderNum,jdbcType=INTEGER} 
    and action like #{action,jdbcType=VARCHAR} 
    and title like #{title,jdbcType=VARCHAR} 
    and icon like #{icon,jdbcType=VARCHAR} 
    and append_class like #{appendClass,jdbcType=VARCHAR} 
    and action_property like #{actionProperty,jdbcType=VARCHAR} 
    and row_property like #{rowProperty,jdbcType=VARCHAR} 
    and display_properties like #{displayProperties,jdbcType=VARCHAR} 
    and confirm_text like #{confirmText,jdbcType=VARCHAR} 
    and is_refresh_list = #{isRefreshList,jdbcType=TINYINT}
