mapper.insert:
insert into cg_form_field (id,form_id,order_num,entity_field,width,group_title,item_properties,form_item_properties,readonly,must_input,icon,href,hidden,validate_as_title,slot_templates,show_type) select SEQUENCE_CG_FORM_FIELD.nextval,cd.* from(
select #{item.formId,jdbcType=VARCHAR},#{item.orderNum,jdbcType=INTEGER},#{item.entityField,jdbcType=VARCHAR},#{item.width,jdbcType=INTEGER},#{item.groupTitle,jdbcType=VARCHAR},#{item.itemProperties,jdbcType=VARCHAR},#{item.formItemProperties,jdbcType=VARCHAR},#{item.readonly,jdbcType=TINYINT},#{item.mustInput,jdbcType=TINYINT},#{item.icon,jdbcType=VARCHAR},#{item.href,jdbcType=VARCHAR},#{item.hidden,jdbcType=TINYINT},#{item.validateAsTitle,jdbcType=TINYINT},#{item.slotTemplates,jdbcType=VARCHAR},#{item.showType,jdbcType=VARCHAR} from dual
) cd

mapper.update:
update cg_form_field
form_id = #{formId,jdbcType=VARCHAR}, order_num = #{orderNum,jdbcType=INTEGER}, entity_field = #{entityField,jdbcType=VARCHAR}, width = #{width,jdbcType=INTEGER}, group_title = #{groupTitle,jdbcType=VARCHAR}, item_properties = #{itemProperties,jdbcType=VARCHAR}, form_item_properties = #{formItemProperties,jdbcType=VARCHAR}, readonly = #{readonly,jdbcType=TINYINT}, must_input = #{mustInput,jdbcType=TINYINT}, icon = #{icon,jdbcType=VARCHAR}, href = #{href,jdbcType=VARCHAR}, hidden = #{hidden,jdbcType=TINYINT}, validate_as_title = #{validateAsTitle,jdbcType=TINYINT}, slot_templates = #{slotTemplates,jdbcType=VARCHAR}, show_type = #{showType,jdbcType=VARCHAR},
where id = #{id,jdbcType=INTEGER}

mapper.select:

where 1=1 
    and id = #{id,jdbcType=INTEGER} 
    and form_id like #{formId,jdbcType=VARCHAR} 
    and order_num = #{orderNum,jdbcType=INTEGER} 
    and entity_field like #{entityField,jdbcType=VARCHAR} 
    and width = #{width,jdbcType=INTEGER} 
    and group_title like #{groupTitle,jdbcType=VARCHAR} 
    and item_properties like #{itemProperties,jdbcType=VARCHAR} 
    and form_item_properties like #{formItemProperties,jdbcType=VARCHAR} 
    and readonly = #{readonly,jdbcType=TINYINT} 
    and must_input = #{mustInput,jdbcType=TINYINT} 
    and icon like #{icon,jdbcType=VARCHAR} 
    and href like #{href,jdbcType=VARCHAR} 
    and hidden = #{hidden,jdbcType=TINYINT} 
    and validate_as_title = #{validateAsTitle,jdbcType=TINYINT} 
    and slot_templates like #{slotTemplates,jdbcType=VARCHAR} 
    and show_type like #{showType,jdbcType=VARCHAR}
