mapper.insert:
insert into cg_list_field (id,list_id,order_num,entity_field,query_mode,fix,expand,overflow_tooltip,align,header_align,width,column_properties,hidden,edit_inline,default_query_value,cell_display_slot,show_type) select SEQUENCE_CG_LIST_FIELD.nextval,cd.* from(
select #{item.listId,jdbcType=VARCHAR},#{item.orderNum,jdbcType=INTEGER},#{item.entityField,jdbcType=VARCHAR},#{item.queryMode,jdbcType=INTEGER},#{item.fix,jdbcType=TINYINT},#{item.expand,jdbcType=TINYINT},#{item.overflowTooltip,jdbcType=TINYINT},#{item.align,jdbcType=VARCHAR},#{item.headerAlign,jdbcType=VARCHAR},#{item.width,jdbcType=INTEGER},#{item.columnProperties,jdbcType=VARCHAR},#{item.hidden,jdbcType=TINYINT},#{item.editInline,jdbcType=TINYINT},#{item.defaultQueryValue,jdbcType=VARCHAR},#{item.cellDisplaySlot,jdbcType=VARCHAR},#{item.showType,jdbcType=VARCHAR} from dual
) cd

mapper.update:
update cg_list_field
list_id = #{listId,jdbcType=VARCHAR}, order_num = #{orderNum,jdbcType=INTEGER}, entity_field = #{entityField,jdbcType=VARCHAR}, query_mode = #{queryMode,jdbcType=INTEGER}, fix = #{fix,jdbcType=TINYINT}, expand = #{expand,jdbcType=TINYINT}, overflow_tooltip = #{overflowTooltip,jdbcType=TINYINT}, align = #{align,jdbcType=VARCHAR}, header_align = #{headerAlign,jdbcType=VARCHAR}, width = #{width,jdbcType=INTEGER}, column_properties = #{columnProperties,jdbcType=VARCHAR}, hidden = #{hidden,jdbcType=TINYINT}, edit_inline = #{editInline,jdbcType=TINYINT}, default_query_value = #{defaultQueryValue,jdbcType=VARCHAR}, cell_display_slot = #{cellDisplaySlot,jdbcType=VARCHAR}, show_type = #{showType,jdbcType=VARCHAR},
where id = #{id,jdbcType=INTEGER}

mapper.select:

where 1=1 
    and id = #{id,jdbcType=INTEGER} 
    and list_id like #{listId,jdbcType=VARCHAR} 
    and order_num = #{orderNum,jdbcType=INTEGER} 
    and entity_field like #{entityField,jdbcType=VARCHAR} 
    and query_mode = #{queryMode,jdbcType=INTEGER} 
    and fix = #{fix,jdbcType=TINYINT} 
    and expand = #{expand,jdbcType=TINYINT} 
    and overflow_tooltip = #{overflowTooltip,jdbcType=TINYINT} 
    and align like #{align,jdbcType=VARCHAR} 
    and header_align like #{headerAlign,jdbcType=VARCHAR} 
    and width = #{width,jdbcType=INTEGER} 
    and column_properties like #{columnProperties,jdbcType=VARCHAR} 
    and hidden = #{hidden,jdbcType=TINYINT} 
    and edit_inline = #{editInline,jdbcType=TINYINT} 
    and default_query_value like #{defaultQueryValue,jdbcType=VARCHAR} 
    and cell_display_slot like #{cellDisplaySlot,jdbcType=VARCHAR} 
    and show_type like #{showType,jdbcType=VARCHAR}
