mapper.update:
update cg_table
code = #{code,jdbcType=VARCHAR}, name = #{name,jdbcType=VARCHAR}, title = #{title,jdbcType=VARCHAR}, sub_package = #{subPackage,jdbcType=VARCHAR}, entity = #{entity,jdbcType=VARCHAR}, template = #{template,jdbcType=VARCHAR}, trial_licence = #{trialLicence,jdbcType=INTEGER}, trial_days = #{trialDays,jdbcType=INTEGER}, action_list = #{actionList,jdbcType=VARCHAR}, imports = #{imports,jdbcType=VARCHAR}, controller_extends = #{controllerExtends,jdbcType=VARCHAR}, pojo_imports = #{pojoImports,jdbcType=VARCHAR}, pojo_extends = #{pojoExtends,jdbcType=VARCHAR}, pojo_java_code = #{pojoJavaCode,jdbcType=VARCHAR}, creator = #{creator,jdbcType=VARCHAR}, flow_dyna_fields_op = #{flowDynaFieldsOp,jdbcType=VARCHAR}, flow_dyna_fields_cp = #{flowDynaFieldsCp,jdbcType=VARCHAR}, project_id = #{projectId,jdbcType=VARCHAR},
