    and cg_table.template like #{template,jdbcType=VARCHAR} 
    and cg_table.trial_licence = #{trialLicence,jdbcType=INTEGER} 
    and cg_table.trial_days = #{trialDays,jdbcType=INTEGER} 
    and cg_table.action_list like #{actionList,jdbcType=VARCHAR} 
    and cg_table.imports like #{imports,jdbcType=VARCHAR} 
    and cg_table.controller_extends like #{controllerExtends,jdbcType=VARCHAR} 
    and cg_table.pojo_imports like #{pojoImports,jdbcType=VARCHAR} 
    and cg_table.pojo_extends like #{pojoExtends,jdbcType=VARCHAR} 
    and cg_table.pojo_java_code like #{pojoJavaCode,jdbcType=VARCHAR} 
