    and cg_table.flow_dyna_fields_op like #{flowDynaFieldsOp,jdbcType=VARCHAR} 
    and cg_table.flow_dyna_fields_cp like #{flowDynaFieldsCp,jdbcType=VARCHAR} 
    and cg_table.project_id like #{projectId,jdbcType=VARCHAR} 
