mapper.insert:
insert into sys_action (id,note,value,params,method) select SEQUENCE_SYS_ACTION.nextval,cd.* from(
select #{item.note,jdbcType=VARCHAR},#{item.value,jdbcType=VARCHAR},#{item.params,jdbcType=VARCHAR},#{item.method,jdbcType=VARCHAR} from dual
) cd

mapper.update:
update sys_action set 
  note = #{note,jdbcType=VARCHAR},
  value = #{value,jdbcType=VARCHAR},
  params = #{params,jdbcType=VARCHAR},
  method = #{method,jdbcType=VARCHAR}
  where id = #{id,jdbcType=INTEGER}

update sys_action
note = NULL, note = #{note,jdbcType=VARCHAR}, value = NULL, value = #{value,jdbcType=VARCHAR}, params = NULL, params = #{params,jdbcType=VARCHAR}, method = NULL, method = #{method,jdbcType=VARCHAR},
where id = #{id,jdbcType=INTEGER}

mapper.select:

where 1=1 
    and note like #{note,jdbcType=VARCHAR} 
    and id = #{id,jdbcType=INTEGER} 
    and value like #{value,jdbcType=VARCHAR} 
    and params like #{params,jdbcType=VARCHAR} 
    and method like #{method,jdbcType=VARCHAR}
