mapper.insert:
insert into sys_message (id,read_time,create_time,receiver_name,sender_name,title,content,url,receiver,sender,event_id) select SEQUENCE_SYS_MESSAGE.nextval,cd.* from(
select #{item.readTime,jdbcType=TIMESTAMP},#{item.createTime,jdbcType=TIMESTAMP},#{item.receiverName,jdbcType=VARCHAR},#{item.senderName,jdbcType=VARCHAR},#{item.title,jdbcType=VARCHAR},#{item.content,jdbcType=VARCHAR},#{item.url,jdbcType=VARCHAR},#{item.receiver,jdbcType=VARCHAR},#{item.sender,jdbcType=VARCHAR},#{item.eventId,jdbcType=VARCHAR} from dual
) cd

mapper.update:
update sys_message
read_time = NULL, read_time = #{readTime,jdbcType=TIMESTAMP}, create_time = NULL, create_time = #{createTime,jdbcType=TIMESTAMP}, receiver_name = NULL, receiver_name = #{receiverName,jdbcType=VARCHAR}, sender_name = NULL, sender_name = #{senderName,jdbcType=VARCHAR}, title = NULL, title = #{title,jdbcType=VARCHAR}, content = NULL, content = #{content,jdbcType=VARCHAR}, url = NULL, url = #{url,jdbcType=VARCHAR}, receiver = NULL, receiver = #{receiver,jdbcType=VARCHAR}, sender = NULL, sender = #{sender,jdbcType=VARCHAR}, event_id = NULL, event_id = #{eventId,jdbcType=VARCHAR},
where id = #{id,jdbcType=INTEGER}

mapper.select:

where 1=1 
    and id = #{id,jdbcType=INTEGER} 
    and read_time like #{readTime,jdbcType=TIMESTAMP} 
    and create_time like #{createTime,jdbcType=TIMESTAMP} 
    and receiver_name like #{receiverName,jdbcType=VARCHAR} 
    and sender_name like #{senderName,jdbcType=VARCHAR} 
    and title like #{title,jdbcType=VARCHAR} 
    and content like #{content,jdbcType=VARCHAR} 
    and url like #{url,jdbcType=VARCHAR} 
    and receiver like #{receiver,jdbcType=VARCHAR} 
    and sender like #{sender,jdbcType=VARCHAR} 
    and event_id like #{eventId,jdbcType=VARCHAR}
