mapper.update:
update sys_log
time = #{time,jdbcType=TIMESTAMP}, keyword = #{keyword,jdbcType=VARCHAR}, user_type = #{userType,jdbcType=VARCHAR}, user_info = #{userInfo,jdbcType=VARCHAR}, note = #{note,jdbcType=VARCHAR},
where id = #{id,jdbcType=VARCHAR}

mapper.select:

where 1=1 
    and id like #{id,jdbcType=VARCHAR} 
    and time like #{time,jdbcType=TIMESTAMP} 
    and keyword like #{keyword,jdbcType=VARCHAR} 
    and user_type like #{userType,jdbcType=VARCHAR} 
    and user_info like #{userInfo,jdbcType=VARCHAR} 
    and note like #{note,jdbcType=VARCHAR}
