mapper.select:

where 1=1 
    and id = #{id,jdbcType=INTEGER} 
    and name like #{name,jdbcType=VARCHAR} 
    and description like #{description,jdbcType=VARCHAR} 
    and scedule_years like #{sceduleYears,jdbcType=VARCHAR} 
    and schedule_months like #{scheduleMonths,jdbcType=VARCHAR} 
    and schedule_days like #{scheduleDays,jdbcType=VARCHAR} 
    and schedule_weeks like #{scheduleWeeks,jdbcType=VARCHAR} 
    and schedule_hours like #{scheduleHours,jdbcType=VARCHAR} 
    and schedule_minutes like #{scheduleMinutes,jdbcType=VARCHAR} 
    and class_name like #{className,jdbcType=VARCHAR} 
    and mothod_name like #{mothodName,jdbcType=VARCHAR} 
    and is_static = #{isStatic,jdbcType=TINYINT} 
    and parames like #{parames,jdbcType=VARCHAR} 
    and is_running = #{isRunning,jdbcType=TINYINT}
