mapper.select:

where 1=1 
    and id like #{id,jdbcType=VARCHAR} 
    and icon like #{icon,jdbcType=VARCHAR} 
    and name like #{name,jdbcType=VARCHAR} 
    and real_name like #{realName,jdbcType=VARCHAR} 
    and sex like #{sex,jdbcType=VARCHAR} 
    and birth_date like #{birthDate,jdbcType=DATE} 
    and reg_time like #{regTime,jdbcType=TIMESTAMP} 
    and mobile_phone like #{mobilePhone,jdbcType=VARCHAR} 
    and email like #{email,jdbcType=VARCHAR} 
    and wechat_openid like #{wechatOpenid,jdbcType=VARCHAR} 
    and org_code = #{orgCode,jdbcType=INTEGER} 
    and org_privilege = #{orgPrivilege,jdbcType=INTEGER} 
    and role_list like #{roleList,jdbcType=VARCHAR} 
    and locked = #{locked,jdbcType=TINYINT} 
    and state = #{state,jdbcType=TINYINT} 
    and id_type = #{idType,jdbcType=INTEGER} 
    and id_number like #{idNumber,jdbcType=VARCHAR} 
    and expired_time like #{expiredTime,jdbcType=DATE} 
    and password_expired_time like #{passwordExpiredTime,jdbcType=DATE} 
    and password like #{password,jdbcType=VARCHAR} 
