mapper.update:
update pm_version_application
flow_state = #{flowState,jdbcType=INTEGER}, flow_register_time = #{flowRegisterTime,jdbcType=TIMESTAMP}, project = #{project,jdbcType=VARCHAR}, application_type = #{applicationType,jdbcType=INTEGER}, customer = #{customer,jdbcType=VARCHAR}, licence = #{licence,jdbcType=VARCHAR}, contract_no = #{contractNo,jdbcType=VARCHAR}, description = #{description,jdbcType=VARCHAR}, version_info = #{versionInfo,jdbcType=VARCHAR}, flow_note = #{flowNote,jdbcType=VARCHAR}, add_file = #{addFile,jdbcType=VARCHAR}, flow_copy_to_list = #{flowCopyToList,jdbcType=VARCHAR}, flow_register_by = #{flowRegisterBy,jdbcType=VARCHAR}, flow_next_operator = #{flowNextOperator,jdbcType=VARCHAR},
where id = #{id,jdbcType=CHAR}

mapper.select:

where 1=1 
    and pm_version_application.id like #{id,jdbcType=CHAR} 
    and pm_version_application.flow_state = #{flowState,jdbcType=INTEGER} 
    and pm_version_application.flow_register_time like #{flowRegisterTime,jdbcType=TIMESTAMP} 
    and pm_version_application.project like #{project,jdbcType=VARCHAR} 
    and pm_version_application.application_type = #{applicationType,jdbcType=INTEGER} 
    and pm_version_application.customer like #{customer,jdbcType=VARCHAR} 
    and pm_version_application.licence like #{licence,jdbcType=VARCHAR} 
    and pm_version_application.contract_no like #{contractNo,jdbcType=VARCHAR} 
    and pm_version_application.description like #{description,jdbcType=VARCHAR} 
    and pm_version_application.version_info like #{versionInfo,jdbcType=VARCHAR} 
    and pm_version_application.flow_note like #{flowNote,jdbcType=VARCHAR} 
    and pm_version_application.add_file like #{addFile,jdbcType=VARCHAR} 
    and pm_version_application.flow_copy_to_list like #{flowCopyToList,jdbcType=VARCHAR} 
    and pm_version_application.flow_register_by like #{flowRegisterBy,jdbcType=VARCHAR} 
    and sys_user.real_name like #{registerByName,jdbcType=VARCHAR} 
    and pm_version_application.flow_next_operator like #{flowNextOperator,jdbcType=VARCHAR} 
    and sys_user1.real_name like #{nextOperatorName,jdbcType=VARCHAR}
