mapper.update:
update dev_event
dev_type = #{devType,jdbcType=VARCHAR}, dev_no = #{devNo,jdbcType=VARCHAR}, org_code = #{orgCode,jdbcType=INTEGER}, status = #{status,jdbcType=INTEGER}, time = #{time,jdbcType=TIMESTAMP}, user_no = #{userNo,jdbcType=VARCHAR},
where id = #{id,jdbcType=VARCHAR}

mapper.select:

where 1=1 
    and dev_event.id like #{id,jdbcType=VARCHAR} 
    and dev_event.dev_type like #{devType,jdbcType=VARCHAR} 
    and dev_event.dev_no like #{devNo,jdbcType=VARCHAR} 
    and dev_event.org_code = #{orgCode,jdbcType=INTEGER} 
    and dev_event.status = #{status,jdbcType=INTEGER} 
    and dev_event.time like #{time,jdbcType=TIMESTAMP} 
    and dev_event.user_no like #{userNo,jdbcType=VARCHAR} 
    and dev_people.real_name like #{realName,jdbcType=VARCHAR}
