mapper.select:
 where 

	parent = 0 or parent is null 
	parent = #{id,jdbcType=INTEGER}

mapper.insert:
insert into sys_menu (id,sort_num,name,parent,is_divider,icon,action,class_name,data_action,bigIcon,mobile_hidden,js_cmd) select SEQUENCE_SYS_MENU.nextval,cd.* from(
select #{item.sortNum,jdbcType=INTEGER},#{item.name,jdbcType=VARCHAR},#{item.parent,jdbcType=INTEGER},#{item.isDivider,jdbcType=TINYINT},#{item.icon,jdbcType=VARCHAR},#{item.action,jdbcType=VARCHAR},#{item.className,jdbcType=VARCHAR},#{item.dataAction,jdbcType=VARCHAR},#{item.bigicon,jdbcType=VARCHAR},#{item.mobileHidden,jdbcType=TINYINT},#{item.jsCmd,jdbcType=VARCHAR} from dual
) cd

mapper.update:
update sys_menu
sort_num = NULL, sort_num = #{sortNum,jdbcType=INTEGER}, name = NULL, name = #{name,jdbcType=VARCHAR}, parent = NULL, parent = #{parent,jdbcType=INTEGER}, is_divider = NULL, is_divider = #{isDivider,jdbcType=TINYINT}, icon = NULL, icon = #{icon,jdbcType=VARCHAR}, action = NULL, action = #{action,jdbcType=VARCHAR}, class_name = NULL, class_name = #{className,jdbcType=VARCHAR}, data_action = NULL, data_action = #{dataAction,jdbcType=VARCHAR}, bigIcon = NULL, bigIcon = #{bigicon,jdbcType=VARCHAR}, mobile_hidden = NULL, mobile_hidden = #{mobileHidden,jdbcType=TINYINT}, js_cmd = NULL, js_cmd = #{jsCmd,jdbcType=VARCHAR},
where id = #{id,jdbcType=INTEGER}

mapper.select:

where 1=1 
    and id = #{id,jdbcType=INTEGER} 
    and sort_num = #{sortNum,jdbcType=INTEGER} 
    and name like #{name,jdbcType=VARCHAR} 
    and parent = #{parent,jdbcType=INTEGER} 
    and is_divider = #{isDivider,jdbcType=TINYINT} 
    and icon like #{icon,jdbcType=VARCHAR} 
    and action like #{action,jdbcType=VARCHAR} 
    and class_name like #{className,jdbcType=VARCHAR} 
    and data_action like #{dataAction,jdbcType=VARCHAR} 
    and bigIcon like #{bigicon,jdbcType=VARCHAR} 
    and mobile_hidden = #{mobileHidden,jdbcType=TINYINT} 
    and js_cmd like #{jsCmd,jdbcType=VARCHAR}
