mapper.update:
update oauth_client_details
client_secret = NULL, client_secret = #{clientSecret,jdbcType=VARCHAR}, scope = NULL, scope = #{scope,jdbcType=VARCHAR}, authorized_grant_types = NULL, authorized_grant_types = #{authorizedGrantTypes,jdbcType=VARCHAR}, web_server_redirect_uri = NULL, web_server_redirect_uri = #{webServerRedirectUri,jdbcType=VARCHAR}, authorities = NULL, authorities = #{authorities,jdbcType=VARCHAR}, access_token_validity = NULL, access_token_validity = #{accessTokenValidity,jdbcType=INTEGER}, refresh_token_validity = NULL, refresh_token_validity = #{refreshTokenValidity,jdbcType=INTEGER}, autoapprove = NULL, autoapprove = #{autoapprove,jdbcType=VARCHAR}, expired_date = NULL, expired_date = #{expiredDate,jdbcType=TIMESTAMP}, locked = NULL, locked = #{locked,jdbcType=TINYINT}, enabled = NULL, enabled = #{enabled,jdbcType=TINYINT}, decription = NULL, decription = #{decription,jdbcType=VARCHAR}, additional_information = NULL, additional_information = #{additionalInformation,jdbcType=VARCHAR}, resource_ids = NULL, resource_ids = #{resourceIds,jdbcType=VARCHAR},
where client_id = #{clientId,jdbcType=VARCHAR}

mapper.select:

where 1=1 
    and client_id like #{clientId,jdbcType=VARCHAR} 
    and client_secret like #{clientSecret,jdbcType=VARCHAR} 
    and scope like #{scope,jdbcType=VARCHAR} 
    and authorized_grant_types like #{authorizedGrantTypes,jdbcType=VARCHAR} 
    and web_server_redirect_uri like #{webServerRedirectUri,jdbcType=VARCHAR} 
    and authorities like #{authorities,jdbcType=VARCHAR} 
    and access_token_validity = #{accessTokenValidity,jdbcType=INTEGER} 
    and refresh_token_validity = #{refreshTokenValidity,jdbcType=INTEGER} 
    and autoapprove like #{autoapprove,jdbcType=VARCHAR} 
    and expired_date like #{expiredDate,jdbcType=TIMESTAMP} 
    and locked = #{locked,jdbcType=TINYINT} 
    and enabled = #{enabled,jdbcType=TINYINT} 
    and decription like #{decription,jdbcType=VARCHAR} 
    and additional_information like #{additionalInformation,jdbcType=VARCHAR} 
    and resource_ids like #{resourceIds,jdbcType=VARCHAR}
